import org.efaps.admin.runlevel.RunLevel;
import org.efaps.db.Context;
import org.efaps.db.SearchQuery;
import org.efaps.esjp.admin.ui.UIUpdate;
import org.efaps.importer.DataImport;

import java.net.URL
import java.io.File;

EFAPS_LOGGER.info("Importing Data for eFaps Issues App");

SearchQuery query = new SearchQuery();
query.setQueryTypes("Admin_User_JAASKey");
query.addWhereExprEqValue("Key", "Issues_Admin");
query.addSelect("OID");
query.execute();
if (!query.next()) {

    RunLevel.init("shell");
    RunLevel.execute();

                URL url3 = new URL(EFAPS_ROOTURL, "Data/IMPORT_UserJAASKey.xml");
    DataImport imp3 = DataImport.readFile(url3);
    imp3.updateInDB();
}
